import React, { useEffect, useState } from 'react';
import {
    FormControlLabel,
    TextField,
    FormLabel,
    Radio,
    RadioGroup,
    Checkbox,
} from '@material-ui/core';
import styles from './Declaration.module.css';

const Declaration = () => {
    const [inputsRad, setInputsRad] = useState({
        diabetes: 'No',
        heart: 'No',
        hernia: 'No',
        allergies: 'No',
        seizures: 'No',
        asthma: 'No',
        sinusitis: 'No',
        hypertension: 'No',
        hypotension: 'No',
        others: 'No',
        surgeryProt: 'No',
        hearing: 'No',
        visual: 'No',
        spine: 'No',
        fainting: 'No',
        psychological: 'No',
        psychiatric: 'No',
        medicines: 'No',
    });
    const [inputsText, setInputsText] = useState({

        diabetesD: '',
        heartD: '',
        herniaD: '',
        allergiesD: '',
        seizuresD: '',
        asthmaD: '',
        sinusitisD: '',
        hypertensionD: '',
        hypotensionD: '',
        othersD: '',
        surgeryProtD: '',
        hearingD: '',
        visualD: '',
        spineD: '',
        faintingD: '',
        psychologicalD: '',
        psychiatricD: '',
        medicinesD: '',
    });

    const [checkInput, setCheckInput] = useState({ accept: '' });

    const handleRad = (e) => {
        let { name, value } = e.target;
        setInputsRad({
            ...inputsRad,
            [name]: value,
        });
    };
    // const handleName = (e) => {
    //     let { name, value } = e.target;
    //     validin(value, 'string');
    //     setInputsText({
    //         ...inputsText,
    //         [name]: value,
    //     });
    // };
    const handleText = (e) => {
        let { name, value } = e.target;
        setInputsText({
            ...inputsText,
            [name]: value,
        });
    };

    const handleCheck = (e) => {
        setCheckInput({ ...checkInput, [e.target.name]: e.target.checked });
    };

    // const validin = (input, type) => {
    //     const ex_name = /^[a-z ,.'-]+$/i;
    //     if (type === 'string') {
    //         if (!ex_name.test(input)) {
    //             setErrors({
    //                 ...errors,
    //                 completeName: 'Completar nombre y apellido',
    //             });
    //         } else {
